	private final static Pattern VALID_EMAIL = Pattern.compile(EMAIL_PATTERN);

	private static final Pattern AT_LEAST_ONE_UPPERCASE = Pattern.compile("(?=.*[a-z])(?=.*[A-Z])");
	
	

	
	private Visitor visitor;
	
	public void setVisitor(Visitor visitor) {
		this.visitor=visitor;
		
	}
	
	

	@Override
	public boolean supports(Class<?> aClass) {
		return EditInfoVisitorForm.class.equals(aClass);
	}

	@Override
	public void validate(Object o, Errors errors) {
		EditInfoVisitorForm editInfoVisitorForm = (EditInfoVisitorForm) o;

		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "fullName", "NotEmpty");
		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "email", "NotEmpty");

		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "nick", "NotEmpty");
		
		if(errors.hasErrors()) {
			return;
		}

		// ValidationUtils.rejectIfEmptyOrWhitespace(errors, "webAddress", "NotEmpty");
		if (editInfoVisitorForm.getOldPassword().isEmpty() && (!editInfoVisitorForm.getRePassword().isEmpty()
				|| !editInfoVisitorForm.getPassword().isEmpty())) {
			errors.rejectValue("oldPassword", "OldPassword.notEmpty");
		}
		
		if(errors.hasErrors()) {
			return;
		}
		
		
		if (!editInfoVisitorForm.getOldPassword().isEmpty() && !(PasswordUtil.passwordEncrypt(editInfoVisitorForm.getOldPassword())).equals(visitor.getPasswordHash())) {
			errors.rejectValue("oldPassword", "OldPassword.notSame");
		}
		
		if(errors.hasErrors()) {
			return;
		}
		
		


		if (!visitor.getEmail().equals(editInfoVisitorForm.getEmail()) && organiserService.findByEmail(editInfoVisitorForm.getEmail()) != null) {
			errors.rejectValue("email", "Duplicate.email");
		}

		if (!editInfoVisitorForm.getPassword().isEmpty() && editInfoVisitorForm.getPassword().length() < 6) {
			errors.rejectValue("password", "Size.password");

		}
		// Matcher m=AT_LEAST_ONE_UPPERCASE.matcher(visitorForm.getPassword());

//        if (m.matches()) {
//        	errors.rejectValue("password", "Password.uppercase");
//        }
		// ovo nisam uspio naci regex -> jebe me sve kaj sam trazio

		if (!checkEmail(editInfoVisitorForm.getEmail())) {
			errors.rejectValue("email", "Invalid.email");
		}

		if (errors.hasErrors()) {
			return;
		}

		if (!editInfoVisitorForm.getRePassword().equals(editInfoVisitorForm.getPassword())) {
			errors.rejectValue("rePassword", "NotSame.Password");

		}

	}

	private static boolean checkEmail(String email) {
		Matcher matcher = VALID_EMAIL.matcher(email);
		return matcher.find();
	}

	

