	private final static Pattern VALID_EMAIL =
            Pattern.compile(EMAIL_PATTERN);
	
	
	@Autowired
	private LoginService loginService;
	

	@Override
	public boolean supports(Class<?> clazz) {
		return LoginForm.class.equals(clazz);
	}
	@Override
	public void validate(Object o, Errors errors) {
		LoginForm loginForm = (LoginForm) o;
		
		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "email", "NotEmpty");
        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "password", "NotEmpty");
        
        if(errors.hasErrors()) {
        	return;
        }
        
        if (!checkEmail(loginForm.getEmail())) {
            errors.rejectValue("email", "Invalid.email");
        }
        
//        if(errors.hasErrors()) {
//        	return;
//        }
//
//     
//        
//        if (loginService.userAccountExists(loginForm)){
//            errors.rejectValue("password", "Wrong.login");
//        }
		
	}
	
	 private static boolean checkEmail(String email) {
	        Matcher matcher = VALID_EMAIL.matcher(email);
	        return matcher.find();
	    }

