	private final static Pattern VALID_EMAIL = Pattern.compile(EMAIL_PATTERN);

	@Override
	public boolean supports(Class<?> aClass) {
		return PayPalForm.class.equals(aClass);
	}

	@Override
	public void validate(Object o, Errors errors) {
		PayPalForm payPalForm = (PayPalForm) o;

		ValidationUtils.rejectIfEmptyOrWhitespace(errors, "payPalEmail", "NotEmpty");
		if (errors.hasErrors()) {
			return;
		}

		if (!checkEmail(payPalForm.getPayPalEmail())) {
			errors.rejectValue("payPalEmail", "Invalid.email");
		}

		// TODO Auto-generated method stub

	}

	private static boolean checkEmail(String email) {
		Matcher matcher = VALID_EMAIL.matcher(email);
		return matcher.find();
	}

