    private final static Pattern VALID_EMAIL =
            Pattern.compile(EMAIL_PATTERN);
    
    private static final Pattern AT_LEAST_ONE_UPPERCASE=Pattern.compile("(?=.*[a-z])(?=.*[A-Z])");
    
    


    @Override
    public boolean supports(Class<?> aClass) {
        return VisitorForm.class.equals(aClass);
    }

    @Override
    public void validate(Object o, Errors errors) {
        VisitorForm visitorForm = (VisitorForm) o;


        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "fullName", "NotEmpty");
        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "nick", "NotEmpty");
        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "password", "NotEmpty");
        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "rePassword", "NotEmpty");
        ValidationUtils.rejectIfEmptyOrWhitespace(errors, "email", "NotEmpty");
        
        if(errors.hasErrors()) {
        	return;
        }

        if (visitorService.findByEmail(visitorForm.getEmail()) != null) {
            errors.rejectValue("email", "Duplicate.email");
        }
        
        if (visitorService.findByNick(visitorForm.getNick()) != null) {
            errors.rejectValue("nick", "Duplicate.visitorForm.nick");
        }
        
       

        if (visitorForm.getPassword().length() < 6 ) {
            errors.rejectValue("password", "Size.password");
            
        }
        Matcher m=AT_LEAST_ONE_UPPERCASE.matcher(visitorForm.getPassword());
        
//        if (m.matches()) {
//        	errors.rejectValue("password", "Password.uppercase");
//        }
        //ovo nisam uspio naci regex -> jebe me sve kaj sam trazio
        
        if (!checkEmail(visitorForm.getEmail())) {
            errors.rejectValue("email", "Invalid.email");
        }
        
        if(errors.hasErrors()) {
        	return;
        }
     
        
        
        if (!visitorForm.getRePassword().equals(visitorForm.getPassword())) {
            errors.rejectValue("rePassword", "NotSame.Password");
            
        }


    }

    private static boolean checkEmail(String email) {
        Matcher matcher = VALID_EMAIL.matcher(email);
        return matcher.find();
    }
   
    

